import React from 'react';
import styled from 'styled-components';
import './WhoComponent.css';
import image1 from '../../../Assets/image1.jpg';
import image2 from '../../../Assets/image2.jpg';
import image3 from '../../../Assets/image3.jpg';
import image4 from '../../../Assets/image4.jpg';

const Section = styled.div`
  height: 100vh;

  scroll-snap-align: center;
  overflow: hidden;
`;

const ParallaxContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: 100vh;
  overflow-y: auto;
`;

const ParallaxImage = styled.div`
  height: 100vh;
  background-size: cover;
  background-repeat: no-repeat;
  background-position: center;
  transition: transform 0.3s ease-out;
  background-color: pink;

`;

const TextOverlay = styled.div`
  position: absolute;
  top: 50%; /* Adjust to vertically center the text */
  left: 50%; /* Adjust to horizontally center the text */
  transform: translate(-50%, -50%);
  color: white;
  font-size: 24px;
  font-weight: bold;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.5); /* Optional: Add a text shadow for better readability */
`;

const WhoComponent = () => {
  const parallaxData = [
    {
      imageUrl: image1,
      parallaxSpeed: 1,
    },
    {
      imageUrl: image2,
      parallaxSpeed: 2,
    },
    {
      imageUrl: image3,
      parallaxSpeed: 3,
    },
    {
      imageUrl: image4,
      parallaxSpeed: 4,
    },
  ];

  return (
    <Section>
      <ParallaxContainer>
      {parallaxData.map((item, index) => (
          <ParallaxImage
            key={index}
            style={{
              backgroundImage: `url("${item.imageUrl}")`,
              transform: `translateY(0px)`,
            }}
          />
        ))}
      </ParallaxContainer>
    </Section>
  );
};

export default WhoComponent;
