import React, { useState, useEffect } from 'react';
import axios from 'axios';
import gallery from '../../Assets/beautiful-scenery-summit-mount-everest-covered-with-snow-white-clouds.jpg'

const DaySchedule = () => {

    const [daywise, setDayWise] = useState([]);

    useEffect(() => {
        // Fetch carousel data using axios and update the state
        const fetchCarouselData = async () => {
            try {
                const response = await axios.get('https://localhost:7026/UserSide/GetDashboardImage');
                console.log(response.data);
                setDayWise(response.data); // Assuming the API response contains the data for carousel items
            } catch (error) {
                console.error(error);
            }
        };

        fetchCarouselData();
    }, []);
    return (
        <div>
            <div>
                <img src={gallery} className='gallery-img' />
            </div>
            <div className='gallery-head'>DAY SCHEDULE</div>

            <MDBCard style={{ maxWidth: '540px' }}>
      <MDBRow className='g-0'>
        <MDBCol md='4'>
          <MDBCardImage src='https://mdbootstrap.com/wp-content/uploads/2020/06/vertical.webp' alt='...' fluid />
        </MDBCol>
        <MDBCol md='8'>
          <MDBCardBody>
            <MDBCardTitle>Card title</MDBCardTitle>
            <MDBCardText>
              This is a wider card with supporting text below as a natural lead-in to additional content. This
              content is a little bit longer.
            </MDBCardText>
            <MDBCardText>
              <small className='text-muted'>Last updated 3 mins ago</small>
            </MDBCardText>
          </MDBCardBody>
        </MDBCol>
      </MDBRow>
    </MDBCard>
        </div>
    )
}

export default DaySchedule
