import React, { useEffect, useState } from 'react'
import styled from 'styled-components';
import { ComposableMap, Geographies, Geography, Marker, Annotation } from "react-simple-maps";
import "./MapComponent.css";
import { MDBIcon } from 'mdb-react-ui-kit';

const Section = styled.div`
height:100vh;
display: flex;
align-items: center;
justify-content: center;
scroll-snap-align:center;
background-color:#86e0e0;
`;

const MapContainer = styled.div`
  position: relative;
  height: 100%;
`;

const AnimatedText = styled.p`
  position: absolute;
  top: 78%;
  left:100px;
  font-size: 36px;
  color: black;
  font-weight: 600;
`;



const MapComponent = () => {
  const [showPopup, setShowPopup] = useState(false);
  const locations = [
    {
      name: "Kerala",
      coordinates: [76.2711, 10.8505],
      markerOffset: -15,
    },
    {
      name: "Goa",
      coordinates: [74.124, 15.2993],
      markerOffset: -15,
    },
    {
      name: "Delhi",
      coordinates: [77.2090, 28.6139],
      markerOffset: 25,
    },
    {
      name: "Kolkata",
      coordinates: [88.3639, 22.5726],
      markerOffset: 25,
    },
    // Add more locations here
  ];


  return (
    <Section id="map-section"  className='sectmap'>
      <div className="map">
      <MapContainer>
        <AnimatedText show={showPopup} className='service'>
          Services we are into:
        </AnimatedText>
      <ComposableMap
          projection="geoMercator"
          projectionConfig={{
            scale: 800,
            center: [82, 22], // Adjust center coordinates to focus on India
          }}
          style={{ height: '100vh' }}
        >
          <Geographies geography="india.json">
            {({ geographies }) =>
              geographies.map((geo) => (
                <Geography key={geo.rsmKey} geography={geo} fill="#afe0f4" stroke="black" />
              ))
            }
          </Geographies>
          {locations.map((location, index) => (
            <Marker key={index} coordinates={location.coordinates}>
                 <g
                fill="blue"
                stroke="#ff5533"
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
                transform="translate(-12, 0)"
                fontSize="10" // Adjust font size
                color="blue" // Change color
                outline="none"
                className='marker'
              >
                {/* <PlaceIcon/> */}
                <MDBIcon fas icon="map-marker-alt" />
                {/* <circle cx="12" cy="10" r="3" /> */}
                {/* <path d="M12 2C6.48 2 2 6.48 2 12a9.957 9.957 0 0 0 7.2 9.54c.65.133 1.04-.283 1.24-.54.2-.267.05-.64-.233-.89-.65-.52-1.04-1.25-1.04-2.1 0-1.54 1.11-2.79 2.5-2.79 1.65 0 2.5 1.54 2.5 2.79 0 .85-.39 1.58-1.03 2.1-.28.25-.44.62-.24.91.2.27.62.66 1.22.54A9.957 9.957 0 0 0 22 12c0-5.52-4.48-10-10-10z" /> */}
              </g>
              <text
                textAnchor="middle"
                y={location.markerOffset}
                style={{ fontFamily: 'Manrope', fill: 'black', fontSize: '10px' }}
              >
                {location.name}
              </text>
            </Marker>
          ))}
        </ComposableMap>
        {/* <p className='service'>Services we are into: </p> */}
        </MapContainer>
      </div>
    </Section>
  )
}

export default MapComponent
