import React, { useState, useEffect } from 'react';
import gallery from '../../Assets/low-angle-shot-mesmerizing-starry-sky.jpg';
import userImage from '../../Assets/user.png'
import './ContactUs.css';
import { MDBInput } from 'mdb-react-ui-kit';
import axios from 'axios';
import {
  MDBCard,
  MDBCardImage,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
} from 'mdb-react-ui-kit';


const ContactUs = () => {
  const [admin,setAdmin]=useState([])
  useEffect(() => {
      // Fetch carousel data using axios and update the state
      const fetchCarouselData = async () => {
          try {
              const response = await axios.get('https://localhost:7026/UserSide/DisplayingAllLocations');
              console.log(response.data);
              setAdmin(response.data); // Assuming the API response contains the data for carousel items
          } catch (error) {
              console.error(error);
          }
      };

      fetchCarouselData();
  }, []);
  return (
    <div>
      <div>
        <img src={gallery} className='gallery-img' alt='Gallery' />
      </div>
      <div className='book-head'>CONTACT US</div>
      <div className='container mt-5 margin-auto'>
        <br /><br /><br />
        <MDBCard className='h-100 widthof-card'>
          <MDBCardImage
            src={userImage} // Replace with the actual image source from the API response
            
            position='top'
            className='cardof-image'
          />
          <MDBCardBody className='location-body'>
            <MDBCardTitle className='location-name'>{admin.name}</MDBCardTitle>
            <MDBCardText><span className='location-price'>{admin.email}</span></MDBCardText>
            <MDBCardText><span className='location-price'>Phone Number: {admin.phoneNumber}</span></MDBCardText>
          </MDBCardBody>
        </MDBCard>
        <br /><br /><br /> <br /><br /><br />
      </div>
    </div>
  )
}

export default ContactUs
