import React, { useState } from 'react';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './UserRegister.css';
import LoginImage from '../../../Assets/10606146_9800.jpg';
import { MDBIcon } from 'mdb-react-ui-kit';


const UserRegister = () => {
    const [userDTO, setUserDTO] = useState({
        name: '',
        username: '',
        userPassword: '',
        isActive:true,
        email: '',
        role: 'User',
        phoneNumber:0
    });
    const [success, setSuccess] = useState(false);
    const [errors, setErrors] = useState({
        name: '',
        email: '',
        username: '',
        phoneNumber: '',
        userPassword:''
    });

    const validateEmail = (email) => {
        // Very basic email validation
        return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
    };

    const validatePassword = (userPassword) => {
        // Password regex pattern: At least one uppercase, one lowercase, one number, and one special character
        const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
        return passwordRegex.test(userPassword);
    };

    const validatePhoneNumber = (phoneNumber) => {
        return /^\d{0,10}$/.test(phoneNumber);
    };

    const register = () => {
        const newErrors = {
            firstName:
                userDTO.name.trim() === ''
                    ? 'First name is required'
                    : userDTO.name.trim().length < 5 || userDTO.name.trim().length > 20
                        ? 'First name must be between 5 and 20 characters'
                        : '',
            email: userDTO.email.trim() === '' ? 'Email is required' : validateEmail(userDTO.email) ? '' : 'Invalid email',
            phoneNumber:
                userDTO.phoneNumber.trim() === ''
                    ? 'Phone number is required'
                    : !validatePhoneNumber(userDTO.phoneNumber)
                        ? 'Invalid phone number (only numbers allowed, max 10 digits)'
                        : '',
            password:
                userDTO.userPassword.trim() === ''
                    ? 'Password is required'
                    : validatePassword(userDTO.userPassword)
                        ? ''
                        : 'Password must contain at least one uppercase, one lowercase, one number, and one special character',
        };

        setErrors(newErrors);

        console.log(userDTO);

        if (Object.values(newErrors).every((error) => error === '')) {
            console.log(userDTO);
            fetch('https://localhost:7026/api/Auth/Register', {
                method: 'POST',
                headers: {
                    accept: 'text/plain',
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(userDTO),
            })
                .then(async (data) => {
                    if (data.status === 200) {
                        var user = await data.json();
                        setSuccess(true);
                        toast.success('Registered successfully!');
                    }
                })
                .catch((error) => {
                    console.error('Error:', error);
                    toast.error('Registration failed!');
                });
        }
    };

    return (
        <>
        <MDBBtn onClick={toggleShow}>Launch frame modal</MDBBtn>

<MDBModal animationDirection='bottom' show={bottomModal} tabIndex='-1' setShow={setBottomModal}>
  <MDBModalDialog position='bottom' frame>
    <MDBModalContent>
      <MDBModalBody className='py-1'>
        <div className='d-flex justify-content-center align-items-center my-3'>
          <p className='mb-0'>We use cookies to improve your website experience</p>
          <MDBBtn color='success' size='sm' className='ms-2' onClick={toggleShow}>
            Ok, thanks
          </MDBBtn>
          <MDBBtn size='sm' className='ms-2'>
            Learn more
          </MDBBtn>
        </div>
      </MDBModalBody>
    </MDBModalContent>
  </MDBModalDialog>
</MDBModal>
        </>

    );
};

export default UserRegister;
