import React from 'react'
import styled from 'styled-components';

const Section=styled.div`
height:100vh;
background-color:red;
scroll-snap-align:center;
`;


const ParallaxContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: 100vh;
  overflow-y: auto; /* Allow scrolling for the parallax effect */
`;

const ParallaxImage = styled.img`
  height: 100vh; /* Set the height of each image to 100vh */
  object-fit: cover; /* Adjust the image sizing to cover the entire container */
  transform: translateY(${({ offset }) => offset}px); /* Apply the parallax effect */
  transition: transform 0.3s ease-out; /* Add a smooth transition effect */
`;

const WhoComponent = () => {
  return (
    <Section>
          <ParallaxContainer>
      {parallaxOffsets.map((offset, index) => (
        <ParallaxImage
          key={index}
          src={`image${index + 1}.jpg`} // Replace with your image URLs
          offset={offset}
        />
      ))}
    </ParallaxContainer>
    </Section>
  )
}

export default WhoComponent
