import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { MDBCarousel, MDBCarouselItem } from 'mdb-react-ui-kit';
import Maldives from '../../../Assets/maldives-island.jpg';
import beach from '../../../Assets/beautiful-tropical-beach-sea.jpg'
import coast from '../../../Assets/coast-idyllic-sand-relax-vacation.jpg'
import axios from 'axios';

const Section=styled.div`
height:100vh;
scroll-snap-align:center;
`;

const HeaderComponent = () => {
  
  const [carouselData, setCarouselData] = useState([]);
  const [imageNames, setImageNames] = useState([]);
  useEffect(() => {
    // Fetch carousel data using axios and update the state
    const fetchCarouselData = async () => {
      try {
        const response = await axios.get('https://localhost:7026/UserSide/GetDashboardImage');
        console.log(response.data);
        setCarouselData(response.data); // Assuming the API response contains the data for carousel items

        // Extract the image names and store them in an array
        const names = response.data.map((item) => item.imageName);
        setImageNames(names);
      } catch (error) {
        console.error(error);
      }
    };

    fetchCarouselData();
  }, []);

  return (
    <Section>
      
      <MDBCarousel showIndicators showControls={false} dealy={10} fade className='CarousalWidth' dark  style={{ height: '100vh' }}>
        <MDBCarouselItem
          className='w-100 d-block'
          itemId={1}
          src={Maldives}
          alt='...'
          style={{height:'100vh',width:'100%'}}
        >
          <h2>First slide label</h2>
          <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
        </MDBCarouselItem>

        <MDBCarouselItem
          className='w-100 d-block '
          itemId={2}
          src={coast}
          alt='...'
          style={{height:'100vh',width:'100%'}}
        >
          <h5>Second slide label</h5>
          <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
        </MDBCarouselItem>

        <MDBCarouselItem
          className='w-100 d-block'
          itemId={3}
          src={beach}
          alt='...'
          style={{height:'100vh',width:'100%'}}
        >
          <h5>Third slide label</h5>
          <p>Praesent commodo cursus magna, vel scelerisque nisl consectetur.</p>
        </MDBCarouselItem>
      </MDBCarousel>


      <MDBCarousel showIndicators showControls={false} delay={10} fade className='CarousalWidth' dark style={{ height: '100vh' }}>
        {carouselData.map((item) => (
          <MDBCarouselItem key={item.id}>
            <img className='d-block w-100' src={`data:image/jpeg;base64,${item.imageName}`} alt='...' style={{ height: '100vh', width: '100%' }} />
            <h5>{item.name}</h5>
            <p>{item.description}</p>
          </MDBCarouselItem>
        ))}
      </MDBCarousel>
    </Section>
  )
}

export default HeaderComponent
